import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { contextname } from './Context';
import { NavLink, useNavigate } from 'react-router-dom';


const theme = createTheme();

export default function SignIn() {
  const navigate = useNavigate();

  const contxt = React.useContext(contextname);
  // const [done,setDone] = React.useState(0);
  var done=0;
  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    console.log({
      email: data.get('email'),
      password: data.get('password'),
    });

    if(data.get('email')===''||data.get('password')===''){
      alert("All fields are mendatory");
    }
    else{

      contxt.users.map(i=>{
        console.log(i.mail);
        if(data.get('password') == i.pass && data.get('email') == i.mail)
        {
            contxt.setLogin(i.name);
            contxt.setLoginid(i.id);
            done =1;
            navigate('/home');
        }
      })
      if(done === 0){
          alert("Invalid credentials");
      }

    }
  };

  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
            {/* <LockOutlinedIcon /> */}
            <img style={{width:"60px"}} alt='' src='https://cdn-icons-png.flaticon.com/24/3959/3959542.png'/>
          </Avatar>
          <Typography component="h1" variant="h5">
            Sign in
          </Typography>
          <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
            <TextField
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              autoFocus
            />
            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
            />
            <FormControlLabel
              control={<Checkbox value="remember" color="primary" />}
              label="Remember me"
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              Sign In
            </Button>
            <Grid container>

              <Grid item>
                <NavLink to='/signup'>
                <Typography variant="body2" sx={{cursor:"pionter"}}>
                Don't have an account? Sign Up
                </Typography>
                </NavLink>

              </Grid>
            </Grid>
          </Box>
        </Box>

      </Container>
    </ThemeProvider>
  );
}
